Scripts.OnEvent:
if event == "Cre ShowAttrib Changed" then
					local lbTotalbindG = SAPI.GetChild(self, "lbTotalbindG");
					local lbTotalbindA = SAPI.GetChild(self, "lbTotalbindA");
					local lbTotalbindC = SAPI.GetChild(self, "lbTotalbindC");
					local _, bindmoney = uiGetMyInfo("Money");
					local gold, silver, sopper = SAPI.GetMoneyShowStyle(bindmoney);
					lbTotalbindG:SetText(tostring(gold));
					lbTotalbindA:SetText(tostring(silver));
					lbTotalbindC:SetText(tostring(sopper));
				end

if event == "Cre ShowAttrib Changed" then
					local lbTotalbindG = SAPI.GetChild(self, "lbTotalbindG");
					local lbTotalbindA = SAPI.GetChild(self, "lbTotalbindA");
					local lbTotalbindC = SAPI.GetChild(self, "lbTotalbindC");
					local _, bindmoney = uiGetMyInfo("Money");
					local gold, silver, sopper = SAPI.GetMoneyShowStyle(bindmoney);
					lbTotalbindG:SetText(tostring(gold));
					lbTotalbindA:SetText(tostring(silver));
					lbTotalbindC:SetText(tostring(sopper));
				end

if event == "Cre ShowAttrib Changed" then
					local lbTotalbindG = SAPI.GetChild(self, "lbTotalbindG");
					local lbTotalbindA = SAPI.GetChild(self, "lbTotalbindA");
					local lbTotalbindC = SAPI.GetChild(self, "lbTotalbindC");
					local _, bindmoney = uiGetMyInfo("Money");
					local gold, silver, sopper = SAPI.GetMoneyShowStyle(bindmoney);
					lbTotalbindG:SetText(tostring(gold));
					lbTotalbindA:SetText(tostring(silver));
					lbTotalbindC:SetText(tostring(sopper));
				end

Scripts.OnLClick:
local show_list = { "lbBuyItem", "btItemBuyImg", "1669", "lbItemBuyName", "lbSaveMoney", "edbSaveMoney", "47859", "lbMoney", --[[ "lbAllGold", "edbBuyG", "lbAllAg", "edbBuyA", "lbAllCu", "edbBuyC", ]] "lbBuyTime", "ckbTiime1", "ckbTiime2", "ckbTiime3", "lbBuytime1", "lbBuytime2", "lbBuyTime3",
} local hide_list = { "13297", "29889", "cbDimond",
} local move_list = { ["lbAllGold"] = {49,253}, ["edbBuyG"] = {75,255}, ["lbAllAg"] = {137,253}, ["edbBuyA"] = {163,254}, ["lbAllCu"] = {195,248}, ["edbBuyC"] = {221,254}, } for _, name in ipairs(show_list) do uiGetglobal("layWorld.frBuyAll.fmBuy."..name):Show(); end for _, name in ipairs(hide_list) do uiGetglobal("layWorld.frBuyAll.fmBuy."..name):Hide(); end for name, pos in pairs(move_list) do uiGetglobal("layWorld.frBuyAll.fmBuy."..name):MoveTo(pos[1], pos[2]); end
